<!-- Carousel -->

<p id="hauptüberschrift"> hoteLouvre </p>
<div id="carousel" class="carousel slide" data-bs-ride="carousel">

    <!-- Indicators/dots -->
    <div class="carousel-indicators">
        <button type="button" data-bs-target="#carousel" data-bs-slide-to="0" class="active"></button>
        <button type="button" data-bs-target="#carousel" data-bs-slide-to="1"></button>
        <button type="button" data-bs-target="#carousel" data-bs-slide-to="2"></button>
    </div>

    <!-- The slideshow/carousel -->
    <div class="carousel-inner">
        <div class="carousel-item active">
            <img src="images/hotellobby.png" alt="Hotellobby" class="d-block w-100">
        </div>
        <div class="carousel-item">
            <img src="images/hotelzimmer.png" alt="Hotelzimmer" class="d-block w-100">
        </div>
        <div class="carousel-item">
            <img src="images/hotelpersonal.png" alt="Hotepersonal" class="d-block w-100">
        </div>
    </div>

    <!-- Left and right controls/icons -->
    <button class="carousel-control-prev" type="button" data-bs-target="#carousel" data-bs-slide="prev">
        <span class="carousel-control-prev-icon"></span>
    </button>
    <button class="carousel-control-next" type="button" data-bs-target="#carousel" data-bs-slide="next">
        <span class="carousel-control-next-icon"></span>
    </button>
</div>



